import {ColorUtilities, DOMUtilities, ReactUtilities, PluginUtilities, PluginUpdateUtilities,
	PluginSettings, PluginContextMenu, PluginTooltip, DiscordPermissions, InternalUtilities,
	DiscordModules, DiscordClasses, DiscordSelectors, GeneralUtilities, Patcher, Logger} from "modules";

	
/** 
 * The main object housing the modules making up this library.
 * @version 0.5.6
 */
window["ZeresLibrary"] = {
	ColorUtilities: ColorUtilities,
	DOMUtilities: DOMUtilities,
	ReactUtilities: ReactUtilities,
	PluginUtilities: PluginUtilities,
	PluginUpdateUtilities: PluginUpdateUtilities,
	PluginSettings: PluginSettings,
	ContextMenu: PluginContextMenu,
	Tooltip: PluginTooltip,
	DiscordPermissions: DiscordPermissions,
	InternalUtilities: InternalUtilities,
	DiscordModules: DiscordModules,
	DiscordClasses: DiscordClasses,
	DiscordSelectors: DiscordSelectors,
	GeneralUtilities: GeneralUtilities,
	Patcher: Patcher,
	Logger: Logger,
	Screen: {
		get width() { return Math.max(document.documentElement.clientWidth, window.innerWidth || 0); },
		get height() { return Math.max(document.documentElement.clientHeight, window.innerHeight || 0); }
	},
	creationTime: Date.now(),
	get isOutdated() { return Date.now() - this.creationTime > 600000; },
	version: "0.5.6"
};

window.ZL = window.ZeresLibrary;

BdApi.clearCSS("PluginLibraryCSS");
BdApi.injectCSS("PluginLibraryCSS", PluginSettings.getCSS() + PluginUtilities.getToastCSS() + PluginUpdateUtilities.getCSS());

jQuery.extend(jQuery.easing, { easeInSine: function (x, t, b, c, d) { return -c * Math.cos(t / d * (Math.PI / 2)) + c + b; }});

Patcher.unpatchAll("ZeresLibrary");
Patcher.before("ZeresLibrary", jQuery.fn, "find", (thisObject, args) => {
	if (args.length && args[0] instanceof DOMUtilities.Selector) args[0] = args[0].toString();
});

Patcher.before("ZeresLibrary", global, "$", (thisObject, args) => {
	if (args.length && args[0] instanceof DOMUtilities.Selector) args[0] = args[0].toString();
});

jQuery.extend(true, global.$, jQuery);

/**
 * Helpful utilities for dealing with colors.
 * @module ColorConverter
 * @version 0.0.2
 */

export default class ColorConverter {

	/**
	 * Will get the red green and blue values of any color string.
	 * @param {string} color - the color to obtain the red, green and blue values of. Can be in any of these formats: #fff, #ffffff, rgb, rgba
	 * @returns {array} - array containing the red, green, and blue values
	 */
	static getRGB(color) {
		var result = /rgb\(\s*([0-9]{1,3})\s*,\s*([0-9]{1,3})\s*,\s*([0-9]{1,3})\s*\)/.exec(color);
		if (result) return [parseInt(result[1]), parseInt(result[2]), parseInt(result[3])];

		result = /rgb\(\s*([0-9]+(?:\.[0-9]+)?)%\s*,\s*([0-9]+(?:\.[0-9]+)?)%\s*,\s*([0-9]+(?:\.[0-9]+)?)%\s*\)/.exec(color);
		if (result) return [parseFloat(result[1]) * 2.55, parseFloat(result[2]) * 2.55, parseFloat(result[3]) * 2.55];

		result = /#([a-fA-F0-9]{2})([a-fA-F0-9]{2})([a-fA-F0-9]{2})/.exec(color);
		if (result) return [parseInt(result[1], 16), parseInt(result[2], 16), parseInt(result[3], 16)];
		
		result = /#([a-fA-F0-9])([a-fA-F0-9])([a-fA-F0-9])/.exec(color);
		if (result) return [parseInt(result[1] + result[1], 16), parseInt(result[2] + result[2], 16), parseInt(result[3] + result[3], 16)];
	}

	/**
	 * Will get the darken the color by a certain percent
	 * @param {string} color - Can be in any of these formats: #fff, #ffffff, rgb, rgba
	 * @param {number} percent - percent to darken the color by (0-100)
	 * @returns {string} - new color in rgb format
	 */
	static darkenColor(color, percent) {
		var rgb = this.getRGB(color);
		
		for(var i = 0; i < rgb.length; i++){
			rgb[i] = Math.round(Math.max(0, rgb[i] - rgb[i] * (percent / 100)));
		}
		
		return 'rgb(' + rgb[0] + ',' + rgb[1] + ',' + rgb[2] + ')';
	}

	/**
	 * Will get the lighten the color by a certain percent
	 * @param {string} color - Can be in any of these formats: #fff, #ffffff, rgb, rgba
	 * @param {number} percent - percent to lighten the color by (0-100)
	 * @returns {string} - new color in rgb format
	 */
	static lightenColor(color, percent) {
		var rgb = this.getRGB(color);
		
		for(var i = 0; i < rgb.length; i++){
			rgb[i] = Math.round(Math.min(255, rgb[i] + rgb[i] * (percent / 100)));
		}
		
		return 'rgb(' + rgb[0] + ',' + rgb[1] + ',' + rgb[2] + ')';
	}

	/**
	 * Converts a color to rgba format string
	 * @param {string} color - Can be in any of these formats: #fff, #ffffff, rgb, rgba
	 * @param {number} alpha - alpha level for the new color
	 * @returns {string} - new color in rgb format
	 */
	static rgbToAlpha(color, alpha) {
		var rgb = this.getRGB(color);		
		return 'rgba(' + rgb[0] + ',' + rgb[1] + ',' + rgb[2] + ',' + alpha + ')';
	}

}

/**
 * Self-made context menus that emulate Discord's own context menus.
 * @module PluginContextMenu
 * @version 0.0.7
 */

import {DiscordClasses, DiscordSelectors} from "./DiscordClassModules";
import {$} from "../vendor";


/** Main menu class for creating custom context menus. */
export class Menu {
    /**
     * 
     * @param {boolean} [scroll=false] - should this menu be a scrolling menu (usually only used for submenus)
     */
	constructor(scroll = false) {
		this.theme = $('.theme-dark').length ? "theme-dark" : "theme-light";
		this.element = $("<div>").addClass(DiscordClasses.ContextMenuClasses.contextMenu).addClass("plugin-context-menu").addClass(this.theme);
		this.scroll = scroll;
		if (scroll) {
			this.scroller = $("<div>").addClass(DiscordClasses.ScrollerClasses.scroller).addClass(DiscordClasses.ContextMenuClasses.scroller);
			this.element.append($("<div>")
				.addClass(DiscordClasses.ScrollerClasses.scrollerWrap)
				.addClass(DiscordClasses.ScrollerClasses.scrollerThemed)
				.addClass(DiscordClasses.ScrollerClasses.themeGhostHairline).append(
					this.scroller
			));
		}
	}
    
    /**
     * Adds an item group to the menu. The group should already be populated.
     * @param {PluginContextMenu.ItemGroup} contextGroup - group to add to the menu
     * @returns {PluginContextMenu.Menu} returns self for chaining
     */
	addGroup(contextGroup) {
		if (this.scroll) this.scroller.append(contextGroup.getElement());
		else this.element.append(contextGroup.getElement());
		return this;
	}
    
    /**
     * Adds items to the context menu directly. It is recommended to add to a group and use 
     * {@link PluginContextMenu.Menu#addGroup} instead to behave as natively as possible.
     * @param {PluginContextMenu.MenuItem} contextItems - list of items to add to the context menu
     * @returns {PluginContextMenu.Menu} returns self for chaining
     */
	addItems(...contextItems) {
		for (var i = 0; i < contextItems.length; i++) {
			if (this.scroll) this.scroller.append(contextItems[i].getElement());
			else this.element.append(contextItems[i].getElement());
		}
		return this;
	}
    
    /**
     * Shows the menu at a specific x and y position. This generally comes from the
     * pointer position on a right click event.
     * @param {number} x - x coordinate for the menu to show at
     * @param {number} y - y coordinate for the menu to show at
     */
	show(x, y) {
		const maxWidth = Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
		const maxHeight = Math.max(document.documentElement.clientHeight, window.innerHeight || 0);
		const mouseX = x;
		const mouseY = y;
		
		let type = this.element.parents(".plugin-context-menu").length > this.element.parents(DiscordSelectors.ContextMenuClasses.contextMenu).length ? ".plugin-context-menu" : DiscordSelectors.ContextMenuClasses.contextMenu;
		var depth = this.element.parents(type).length;
		if (depth == 0) this.element.appendTo('#app-mount');
		this.element.css("top", mouseY).css("left", mouseX);
		
		if (depth > 0) {
			var top = this.element.parents(type).last();
			var closest = this.element.parents(type).first();
			var negate = closest.hasClass(DiscordClasses.ContextMenuClasses.invertChildX) ? -1 : 1;
			this.element.css("margin-left", negate * closest.find(`.${DiscordClasses.ContextMenuClasses.item}`).outerWidth() + closest.offset().left - top.offset().left);
		}
		
		if (mouseY + this.element.outerHeight() >= maxHeight) {
			this.element.addClass("invertY").addClass(DiscordClasses.ContextMenuClasses.invertY);
			this.element.css("top", mouseY - this.element.outerHeight());
			if (depth > 0) this.element.css("top", (mouseY + this.element.parent().outerHeight()) - this.element.outerHeight());
		}
		if (this.element.offset().left + this.element.outerWidth() >= maxWidth) {
			this.element.addClass("invertX");
			this.element.css("left", mouseX - this.element.outerWidth());
		}
		if (this.element.offset().left + 2 * this.element.outerWidth() >= maxWidth) {
			this.element.addClass(DiscordClasses.ContextMenuClasses.invertChildX);
		}

		if (depth == 0) {
			$(document).on("mousedown.zctx", (e) => {
				if (!this.element.has(e.target).length && !this.element.is(e.target)) {
					this.removeMenu();
				}
			});
			$(document).on("click.zctx", (e) => {
				if (this.element.has(e.target).length) {
					if ($._data($(e.target).closest(DiscordSelectors.ContextMenuClasses.item)[0], 'events').click) {
						this.removeMenu();
					}
				}
			});
			$(document).on("keyup.zctx", (e) => {
				if (e.keyCode === 27) {
					this.removeMenu();
				}
			});
		}
	}
    
    /** Allows you to remove the menu. */
	removeMenu() {
		let type = this.element.parents(".plugin-context-menu").length > this.element.parents(DiscordSelectors.ContextMenuClasses.contextMenu).length ? ".plugin-context-menu" : DiscordSelectors.ContextMenuClasses.contextMenu;
		this.element.detach();
		this.element.find(type).detach();
		$(document).off(".zctx");
	}
    
    /**
     * Used to attach a menu to a menu item. This is how to create a submenu.
     * If using {@link PluginContextMenu.SubMenuItem} then you do not need
     * to call this function as it is done automatically. If you want to attach
     * a submenu to an existing Discord context menu, then you should use this
     * method.
     * @param {(HTMLElement|jQuery)} menuItem - item to attach to
     */
	attachTo(menuItem) {
		this.menuItem = $(menuItem);
		menuItem.on("mouseenter", () => {
			this.element.appendTo(menuItem);
			let type = this.element.parents(".plugin-context-menu").length > this.element.parents(DiscordSelectors.ContextMenuClasses.contextMenu).length ? ".plugin-context-menu" : DiscordSelectors.ContextMenuClasses.contextMenu;
			this.show(this.element.parents(type).css("left"), menuItem.offset().top);
		});
		menuItem.on("mouseleave", () => { this.element.detach(); });
	}
}

/** Class that represents a group of menu items. */
export class ItemGroup {
    /** Creates an item group. */
	constructor() {
		this.element = $("<div>").addClass(DiscordClasses.ContextMenuClasses.itemGroup);
	}
    
    /**
     * This is the method of adding menu items to a menu group.
     * @param {PluginContextMenu.MenuItem} contextItems - list of context menu items to add to this group
     * @returns {PluginContextMenu.ItemGroup} returns self for chaining
     */
	addItems(...contextItems) {
		for (var i = 0; i < contextItems.length; i++) {
			this.element.append(contextItems[i].getElement());
		}
		return this;
	}
    
    /** @returns {HTMLElement} returns the DOM node for the group */
	getElement() { return this.element; }
}

/**
 * Fires when the attached menu item it clicked.
 * @param {MouseEvent} event - the mouse event from clicking the item
 * @callback PluginContextMenu~clickEvent
 */

 /**
 * Fires when the checkbox item changes state.
 * @param {boolean} isChecked - if the checkbox is now checked
 * @callback PluginContextMenu~onChange
 */

/** Base class for all other menu items. */
export class MenuItem {
    /**
     * @param {string} label - label to show on the menu item
     * @param {object} options - additional options for the item
     * @param {boolean} [options.danger=false] - should the item show as danger
     * @param {PluginContextMenu~clickEvent} [options.callback] - callback for when it is clicked
     */
	constructor(label, options = {}) {
		var {danger = false, callback} = options;
		this.element = $("<div>").addClass(DiscordClasses.ContextMenuClasses.item);
		this.label = label;
		if (danger) this.element.addClass(DiscordClasses.ContextMenuClasses.danger);
		if (typeof(callback) == 'function') {
			this.element.on("click", (event) => {
				event.stopPropagation();
				callback(event);
			});
		}
	}
	getElement() { return this.element;}
}

/** 
 * Creates a text menu item that can have a hint.
 * @extends MenuItem
 */
export class TextItem extends MenuItem {
    /**
     * @param {string} label - label to show on the menu item
     * @param {object} options - additional options for the item
     * @param {string} [options.hint=""] - hint to show on the item (usually used for key combos)
     * @param {boolean} [options.danger=false] - should the item show as danger
     * @param {PluginContextMenu~clickEvent} [options.callback] - callback for when it is clicked
     */
	constructor(label, options = {}) {
		super(label, options);
		var {hint = ""} = options;
		this.element.append($("<span>").text(label));
		this.element.append($("<div>").addClass(DiscordClasses.ContextMenuClasses.hint).text(hint));
	}
}

/** 
 * Creates an image menu item that can have an image.
 * @extends MenuItem
 */
export class ImageItem extends MenuItem {
    /**
     * @param {string} label - label to show on the menu item
     * @param {string} imageSrc - link to the image to embed
     * @param {object} options - additional options for the item
     * @param {string} [options.hint=""] - hint to show on the item (usually used for key combos)
     * @param {boolean} [options.danger=false] - should the item show as danger
     * @param {PluginContextMenu~clickEvent} [options.callback] - callback for when it is clicked
     */
	constructor(label, imageSrc, options = {}) {
		super(label, options);
		this.element.addClass(DiscordClasses.ContextMenuClasses.itemImage);
		this.element.append($("<div>").addClass(DiscordClasses.ContextMenuClasses.label).text(label));
		this.element.append($("<img>", {src: imageSrc}));
	}
}

/** 
 * Creates a menu item with an attached submenu.
 * @extends .MenuItem
 */
export class SubMenuItem extends MenuItem {
    /**
     * @param {string} label - label to show on the menu item
     * @param {PluginContextMenu.Menu} subMenu - context menu that should be attached to this item
     * @param {object} options - additional options for the item
     * @param {string} [options.hint=""] - hint to show on the item (usually used for key combos)
     * @param {boolean} [options.danger=false] - should the item show as danger
     * @param {PluginContextMenu~clickEvent} [options.callback] - callback for when it is clicked
     */
	constructor(label, subMenu, options = {}) {
		// if (!(subMenu instanceof ContextSubMenu)) throw "subMenu must be of ContextSubMenu type.";
		super(label, options);
		this.element.addClass(DiscordClasses.ContextMenuClasses.itemSubMenu).text(label);
		this.subMenu = subMenu;
		this.subMenu.attachTo(this.getElement());
	}
}

/** 
 * Creates a menu item with a checkbox.
 * @extends MenuItem
 */
export class ToggleItem extends MenuItem {
    /**
     * @param {string} label - label to show on the menu item
     * @param {boolean} checked - should the item start out checked
     * @param {object} options - additional options for the item
     * @param {string} [options.hint=""] - hint to show on the item (usually used for key combos)
     * @param {boolean} [options.danger=false] - should the item show as danger
     * @param {PluginContextMenu~clickEvent} [options.callback] - callback for when it is clicked
     * @param {PluginContextMenu~onChange} [options.onChange] - callback for when the checkbox changes
     */
	constructor(label, checked, options = {}) {
        var {onChange} = options;
		super(label, options);
		this.element.addClass(DiscordClasses.ContextMenuClasses.itemToggle);
        this.element.append($("<div>").addClass(DiscordClasses.ContextMenuClasses.label).text(label));
        this.checkbox = $("<div>", {class: "checkbox"});
        this.checkbox.append($("<div>", {class: "checkbox-inner"}));
        this.checkbox.append("<span>");
        this.input = $("<input>", {type: "checkbox", checked: checked, value: "on"});
        this.checkbox.find('.checkbox-inner').append(this.input).append("<span>");
        this.element.append(this.checkbox);
        this.element.on('click', (e) => {
            e.stopPropagation();
            this.input.prop("checked", !this.input.prop("checked"));
            if (typeof(onChange) == 'function') onChange(this.input.prop("checked"));
        });
	}
}

/**
 * Helpful utilities for dealing with getting react information from DOM objects.
 * @module DiscordAPI
 * @version 0.0.4
 */

import { WebpackModules } from './webpackmodules';

class List extends Array {

    constructor() {
        super(...arguments);
    }

    get(...filters) {
        return this.find(item => {
            for (let filter of filters) {
                for (let key in filter) {
                    if (filter.hasOwnProperty(key)) {
                        if (item[key] !== filter[key]) return false;
                    }
                }
            }
            return true;
        });
    }
}

class PermissionsError extends Error {
    constructor(message) {
        super(message);
        this.name = 'PermissionsError';
    }
}

class InsufficientPermissions extends PermissionsError {
    constructor(message) {
        super(`Missing Permission  ${message}`);
        this.name = 'InsufficientPermissions';
    }
}

const Modules = {
    _getModule(name) {
        const foundModule = WebpackModules.getModuleByName(name);
        if (!foundModule) return null;
        delete this[name];
        return this[name] = foundModule;
    },
    get ChannelSelector() { return this._getModule('ChannelSelector'); },
    get MessageActions() { return this._getModule('MessageActions'); },
    get MessageParser() { return this._getModule('MessageParser'); },
    get MessageStore() { return this._getModule('MessageStore'); },
    get EmojiUtils() { return this._getModule('EmojiUtils'); },
    get PermissionUtils() { return this._getModule('Permissions'); },
    get SortedGuildStore() { return this._getModule('SortedGuildStore'); },
    get PrivateChannelActions() { return this._getModule('PrivateChannelActions'); },
    get GuildMemberStore() { return this._getModule('GuildMemberStore'); },
    get GuildChannelsStore() { return this._getModule('GuildChannelsStore'); },
    get MemberCountStore() { return this._getModule('MemberCountStore'); },
    get GuildActions() { return this._getModule('GuildActions'); },
    get NavigationUtils() { return this._getModule('NavigationUtils'); },
    get GuildPermissions() { return this._getModule('GuildPermissions'); },
    get DiscordConstants() { return this._getModule('DiscordConstants'); },
    get ChannelStore() { return this._getModule('ChannelStore'); },
    get GuildStore() { return this._getModule('GuildStore'); },
    get SelectedGuildStore() { return this._getModule('SelectedGuildStore'); },
    get SelectedChannelStore() { return this._getModule('SelectedChannelStore'); },
    get UserStore() { return this._getModule('UserStore'); },
    get RelationshipStore() { return this._getModule('RelationshipStore'); },
    get RelationshipManager() { return this._getModule('RelationshipManager'); },

    get DiscordPermissions() { return this.DiscordConstants.Permissions; }

};

class User {
    constructor(data) {
        for (let key in data)
            if (data.hasOwnProperty(key))
                this[key] = data[key];
        this.discordObject = data;
    }

    static fromId(id) {
        return new User(Modules.UserStore.getUser(id));
    }

    async sendMessage(content, parse = true) {
        const id = await Modules.PrivateChannelActions.ensurePrivateChannel(DiscordAPI.currentUser.id, this.id);
        const channel = new PrivateChannel(Modules.ChannelStore.getChannel(id));
        channel.sendMessage(content, parse);
    }

    get isFriend() {
        return Modules.RelationshipStore.isFriend(this.id);
    }

    get isBlocked() {
        return Modules.RelationshipStore.isBlocked(this.id);
    }

    addFriend() {
        Modules.RelationshipManager.addRelationship(this.id, {location: 'Context Menu'});
    }

    removeFriend() {
        Modules.RelationshipManager.removeRelationship(this.id, {location: 'Context Menu'});
    }

    block() {
        Modules.RelationshipManager.addRelationship(this.id, {location: 'Context Menu'}, Modules.DiscordConstants.RelationshipTypes.BLOCKED);
    }

    unblock() {
        Modules.RelationshipManager.removeRelationship(this.id, {location: 'Context Menu'});
    }
}

class Member extends User {
    constructor(data, guild) {
        super(data);
        const userData = Modules.UserStore.getUser(data.userId);
        for (let key in userData)
            if (userData.hasOwnProperty(key))
                this[key] = userData[key];
        this.guild_id = guild;
    }

    checkPermissions(perms) {
        return Modules.PermissionUtils.can(perms, DiscordAPI.currentUser, Modules.GuildStore.getGuild(this.guild_id));
    }

    kick(reason = '') {
        if (!this.checkPermissions(Modules.DiscordPermissions.KICK_MEMBERS)) throw new InsufficientPermissions('KICK_MEMBERS');
        Modules.GuildActions.kickUser(this.guild_id, this.id, reason);
    }

    ban(daysToDelete = '1', reason = '') {
        if (!this.checkPermissions(Modules.DiscordPermissions.BAN_MEMBERS)) throw new InsufficientPermissions('BAN_MEMBERS');
        Modules.GuildActions.banUser(this.guild_id, this.id, daysToDelete, reason);
    }

    unban() {
        if (!this.checkPermissions(Modules.DiscordPermissions.BAN_MEMBERS)) throw new InsufficientPermissions('BAN_MEMBERS');
        Modules.GuildActions.unbanUser(this.guild_id, this.id);
    }

    move(channel_id) {
        if (!this.checkPermissions(Modules.DiscordPermissions.MOVE_MEMBERS)) throw new InsufficientPermissions('MOVE_MEMBERS');
        Modules.GuildActions.setChannel(this.guild_id, this.id, channel_id);
    }

    mute(active = true) {
        if (!this.checkPermissions(Modules.DiscordPermissions.MUTE_MEMBERS)) throw new InsufficientPermissions('MUTE_MEMBERS');
        Modules.GuildActions.setServerMute(this.guild_id, this.id, active);
    }

    unmute() {
        this.mute(false);
    }

    deafen(active = true) {
        if (!this.checkPermissions(Modules.DiscordPermissions.DEAFEN_MEMBERS)) throw new InsufficientPermissions('DEAFEN_MEMBERS');
        Modules.GuildActions.setServerDeaf(this.guild_id, this.id, active);
    }

    undeafen() {
        this.deafen(false);
    }
}

class Guild {
    constructor(data) {
        for (let key in data)
            if (data.hasOwnProperty(key))
                this[key] = data[key];
        this.discordObject = data;
    }

    get channels() {
        const channels = Modules.GuildChannelsStore.getChannels(this.id);
        const returnChannels = new List();
        for (const category in channels) {
            if (channels.hasOwnProperty(category)) {
                if (!Array.isArray(channels[category])) continue;
                const channelList = channels[category];
                for (const channel of channelList) {
                    returnChannels.push(new GuildChannel(channel.channel));
                }
            }
        }
        return returnChannels;
    }

    get defaultChannel() {
        return new GuildChannel(Modules.GuildChannelsStore.getDefaultChannel(this.id));
    }

    get members() {
        const members = Modules.GuildMemberStore.getMembers(this.id);
        const returnMembers = new List();
        for (const member of members) returnMembers.push(new Member(member, this.id));
        return returnMembers;
    }

    get memberCount() {
        return Modules.MemberCountStore.getMemberCount(this.id);
    }

    get emojis() {
        return Modules.EmojiUtils.getGuildEmoji(this.id);
    }

    get permissions() {
        return Modules.GuildPermissions.getGuildPermissions(this.id);
    }

    getMember(userId) {
        return Modules.GuildMemberStore.getMember(this.id, userId);
    }

    isMember(userId) {
        return Modules.GuildMemberStore.isMember(this.id, userId);
    }

    markAsRead() {
        Modules.GuildActions.markGuildAsRead(this.id);
    }

    select() {
        Modules.GuildActions.selectGuild(this.id);
    }

    nsfwAgree() {
        Modules.GuildActions.nsfwAgree(this.id);
    }

    nsfwDisagree() {
        Modules.GuildActions.nsfwDisagree(this.id);
    }

    changeSortLocation(index) {
        Modules.GuildActions.move(DiscordAPI.guildPositions.indexOf(this.id), index);
    }
}

class Channel {
    constructor(data) {
        for (let key in data)
            if (data.hasOwnProperty(key))
                this[key] = data[key];
        this.discordObject = data;
    }

    checkPermissions(perms) {
        return Modules.PermissionUtils.can(perms, DiscordAPI.currentUser, this.discordObject) || this.isPrivate();
    }

    async sendMessage(content, parse = true) {
        if (!this.checkPermissions(Modules.DiscordPermissions.VIEW_CHANNEL | Modules.DiscordPermissions.SEND_MESSAGES)) throw new InsufficientPermissions('SEND_MESSAGES');
        let response = {};
        if (parse) response = await Modules.MessageActions._sendMessage(this.id, Modules.MessageParser.parse(this.discordObject, content));
        else response = await Modules.MessageActions._sendMessage(this.id, {content});
        return new Message(Modules.MessageStore.getMessage(this.id, response.body.id));
    }

    get messages() {
        const messages = Modules.MessageStore.getMessages(this.id).toArray();
        for (let i in messages)
            if (messages.hasOwnProperty(i))
                messages[i] = new Message(messages[i]);
        return new List(...messages);
    }

    jumpToPresent() {
        if (!this.checkPermissions(Modules.DiscordPermissions.VIEW_CHANNEL)) throw new InsufficientPermissions('VIEW_CHANNEL');
        if (this.hasMoreAfter) Modules.MessageActions.jumpToPresent(this.id, Modules.DiscordConstants.MAX_MESSAGES_PER_CHANNEL);
        else this.messages[this.messages.length - 1].jumpTo(false);
    }

    get hasMoreAfter() {
        return Modules.MessageStore.getMessages(this.id).hasMoreAfter;
    }

    sendInvite(inviteId) {
        if (!this.checkPermissions(Modules.DiscordPermissions.VIEW_CHANNEL | Modules.DiscordPermissions.SEND_MESSAGES)) throw new InsufficientPermissions('SEND_MESSAGES');
        Modules.MessageActions.sendInvite(this.id, inviteId);
    }

    select() {
        if (!this.checkPermissions(Modules.DiscordPermissions.VIEW_CHANNEL)) throw new InsufficientPermissions('VIEW_CHANNEL');
        Modules.NavigationUtils.transitionToGuild(this.guild_id ? this.guild_id : Modules.DiscordConstants.ME, this.id);
    }
}

class GuildChannel extends Channel {

    constructor(data) {
        super(data);
    }

    get permissions() {
        return Modules.GuildPermissions.getChannelPermissions(this.id);
    }

    get guild() {
        return new Guild(Modules.GuildStore.getGuild(this.guild_id));
    }

    isDefaultChannel() {
        return Modules.GuildChannelsStore.getDefaultChannel(this.guild_id).id === this.id;
    }

}

class PrivateChannel extends Channel {
    constructor(data) {
        super(data);
    }
}

class Message {
    constructor(data) {
        for (let key in data)
            if (data.hasOwnProperty(key))
                this[key] = data[key];
        this.discordObject = data;
    }

    delete() {
        Modules.MessageActions.deleteMessage(this.channel_id, this.id);
    }

    // programmatically update the content
    edit(content, parse = false) {
        if (this.author.id !== DiscordAPI.currentUser.id) return;
        if (parse) Modules.MessageActions.editMessage(this.channel_id, this.id, Modules.MessageParser.parse(this.discordObject, content));
        else Modules.MessageActions.editMessage(this.channel_id, this.id, {content});
    }

    // start the editing mode of GUI
    startEdit() {
        if (this.author.id !== DiscordAPI.currentUser.id) return;
        Modules.MessageActions.startEditMessage(this.channel_id, this.id, this.content);
    }

    // end editing mode of GUI
    endEdit() {
        Modules.MessageActions.endEditMessage();
    }

    jumpTo(flash = true) {
        Modules.MessageActions.jumpToMessage(this.channel_id, this.id, flash);
    }
}

export default class DiscordAPI {

    static get channels() {
        const channels = Modules.ChannelStore.getChannels();
        const returnChannels = new List();
        for (const value of Object.values(channels)) {
            returnChannels.push(value.isPrivate() ? new PrivateChannel(value) : new GuildChannel(value));
        }
        return returnChannels;
    }

    static get guilds() {
        const guilds = Modules.GuildStore.getGuilds();
        const returnGuilds = new List();
        for (const value of Object.values(guilds)) {
            returnGuilds.push(new Guild(value));
        }
        return returnGuilds;
    }

    static get users() {
        const users = Modules.UserStore.getUsers();
        const returnUsers = new List();
        for (const value of Object.values(users)) {
            returnUsers.push(new User(value));
        }
        return returnUsers;
    }

    static get memberCounts() {
        return Modules.MemberCountStore.getMemberCounts();
    }

    static get sortedGuilds() {
        const guilds = Modules.SortedGuildStore.getSortedGuilds();
        const returnGuilds = new List();
        for (const guild of guilds) {
            returnGuilds.push(new Guild(guild));
        }
        return returnGuilds;
    }

    static get guildPositions() {
        return Modules.SortedGuildStore.guildPositions;
    }

    static get currentGuild() {
        return new Guild(Modules.GuildStore.getGuild(Modules.SelectedGuildStore.getGuildId()));
    }

    static get currentChannel() {
        const channel = Modules.ChannelStore.getChannel(Modules.SelectedChannelStore.getChannelId());
        if (channel) return channel.isPrivate() ? new PrivateChannel(channel) : new GuildChannel(channel);
    }

    static get currentUser() {
        return Modules.UserStore.getCurrentUser();
    }

    static get friends() {
        const friends = Modules.RelationshipStore.getFriendIDs();
        const returnUsers = new List();
        for (const id of friends) returnUsers.push(User.fromId(id));
        return returnUsers;
    }

}

